import * as THREE from 'three';
import { OrbitControls } from 'three/examples/jsm/controls/OrbitControls'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import * as dat from 'dat.gui';

const gravity = 9.8;
const p0 = 1.225;
const cD = 0.9;
let height;
let air_density;
let parachute_area;
let person_mass;
let velocity_x = 0;
let velocity_y = 0;
let free_velocity_y = 0;
let parachute_mass;
let rope_counts;
let rope_length;
let rope_area;
let side_factor = 1.2;
let free_acc_y = 0;
let para = {};
let bag = {}
let variant = 0;
/**
 * Debug
 * 
 * Mass of Human (change on scale)          Done
 * Mass of Parachute (change on scale)      Done
 * wind_velocity                            Done
 * rope_counts
 * rope_length
 * rope_area
 * Young's factor                           Done
 * parachute_area                           Done
 * height                                   Done
*/
const gui = new dat.GUI();

const parachute_jump = {
    value: 2800
}

const wind = {
    velocity: 10
}

const Young = {
    factor: 3.5
}

gui.add(parachute_jump, 'value');
gui.add(wind, 'velocity');
gui.add(Young, 'factor');

var scene, camera, renderer, light, heights = [], clock;

let man = [], test_box = [];

function initScene()
{
    scene = new THREE.Scene();

    camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 10, 30000);
    camera.position.set(122, 2742, -3000);
    camera.lookAt(new THREE.Vector3(0, 10000, 1000))

    renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // mouse control
    let controls = new OrbitControls(camera, renderer.domElement);
    controls.addEventListener('change', renderer);

    //skybox
    let materialarray = [];

    let texture1 = new THREE.TextureLoader().load('arid2_ft.jpg');
    let texture2 = new THREE.TextureLoader().load('arid2_bk.jpg');
    let texture3 = new THREE.TextureLoader().load('arid2_up.jpg');
    let texture4 = new THREE.TextureLoader().load('arid2_dn.jpg');
    let texture5 = new THREE.TextureLoader().load('arid2_rt.jpg');
    let texture6 = new THREE.TextureLoader().load('arid2_lf.jpg');

    materialarray.push(new THREE.MeshBasicMaterial({ map: texture1 }));
    materialarray.push(new THREE.MeshBasicMaterial({ map: texture2 }));
    materialarray.push(new THREE.MeshBasicMaterial({ map: texture3 }));
    materialarray.push(new THREE.MeshBasicMaterial({ map: texture4 }));
    materialarray.push(new THREE.MeshBasicMaterial({ map: texture5 }));
    materialarray.push(new THREE.MeshBasicMaterial({ map: texture6 }));

    for (let i = 0; i < 6; i++)
        materialarray[i].side = THREE.BackSide;

    let skyboxgeo = new THREE.BoxGeometry(10000, 5000, 10000);
    let skybox = new THREE.Mesh(skyboxgeo, materialarray);
    skybox.position.y = 2500;
    scene.add(skybox);

    let boxgeo = new THREE.BoxGeometry(100, 100, 100);
    let box = new THREE.Mesh(boxgeo, materialarray);
    box.position.y = 4000;
    scene.add(box);
    heights.push(box);
    test_box.push(box);

    //air_plane model
    const loader1 = new GLTFLoader();
    loader1.load('assets/cesium__air.glb', function (glb)
    {
        const air_plane = glb.scene;

        air_plane.scale.set(100, 100, 100);
        air_plane.position.y = 3000;

        scene.add(air_plane);
        heights.push(air_plane)
    },
        undefined, function (error)
    {
        console.error(error);
    });

    // man_player model
    const loader2 = new GLTFLoader();
    loader2.load('assets/man_player.glb', function (glb)
    {
        const man_player = glb.scene;

        man_player.scale.set(100, 100, 100);
        man_player.position.y = 2850;

        scene.add(man_player);

        gui.add(man_player.scale, 'z', 60, 100, 1).onChange(() =>
        {
            person_mass = man_player.scale.z;
        })
            .name('Person Mass');
        person_mass = man_player.scale.z;

        // man_player.scale
        heights.push(man_player);
        man.push(man_player);
    },
        undefined, function (error)
    {
        console.error(error);
    });

    //parachute model
    const loader3 = new GLTFLoader();
    loader3.load('assets/parachute_gta_san_andreas.glb', function (glb)
    {
        const parachute_gta_san_andreas = glb.scene;

        parachute_gta_san_andreas.scale.set(80, 80, 80);
        bag = parachute_gta_san_andreas
        parachute_gta_san_andreas.position.y = 3000;
        parachute_gta_san_andreas.translateZ(-25);

        scene.add(parachute_gta_san_andreas);

        gui.add(parachute_gta_san_andreas.scale, 'z', 40, 160, 1).onChange(() =>
        {
            parachute_mass = parachute_gta_san_andreas.scale.z / 8;
        })
            .name('Parachute Mass');// รท8
        parachute_mass = parachute_gta_san_andreas.scale.z / 8;

        heights.push(parachute_gta_san_andreas);
        man.push(parachute_gta_san_andreas)
    },
        undefined, function (error)
    {
        console.error(error);
    });

    const first_height = {
        y: 0
    }
    gui.add(first_height, 'y', -1000, 4500, 1).onChange(function setHeight(value)
    {
        heights.forEach(w =>
        {
            w.position.y = value;
        });
        heights.at(0).translateY(-50)
        heights.at(2).translateY(-150)
    })

    const first_loader4 = new GLTFLoader();
    first_loader4.load('assets/parachute.glb', function (glb)
    {
        const parachute = glb.scene;
        parachute.scale.set(40, 40, 40);

        parachute_area = parachute.scale.x / 4;
        gui.add(parachute.scale, 'x', 40, 100, 10).onChange(() =>
        {
            parachute_area = parachute.scale.x / 4;
        })
            .name('Parachute Area');// รท4

        para = parachute
    },
        undefined, function (error)
    {
        console.error(error);
    });

    light = new THREE.DirectionalLight(0xffffff, 1)
    light.position.set(2, 2, -5)
    scene.add(light);

    clock = new THREE.Clock();
    animate();
}

function animate()
{
    const delta = clock.getDelta();

    man.forEach(t =>
    {
        let mass = person_mass + parachute_mass;
        let weight_force = mass * gravity;
        air_density = p0 * Math.exp(-t.position.y / 8000);

        if (t.position.y > 0)
        {
            let wind_force = side_factor * (wind.velocity - velocity_x);
            let acc_x = wind_force / (mass || 110);

            console.log(`acc_x in Height: ${t.position.y.toPrecision(4)} is ${acc_x}`);

            velocity_x += acc_x;
            console.log(velocity_x);
            console.log(`velocity_x in Height: ${t.position.y.toPrecision(4)} is ${velocity_x}`);

            t.position.x += velocity_x
            console.log(`x in Height: ${t.position.y.toPrecision(4)} is ${t.position.x}`);
        }

        if (t.position.y > parachute_jump.value)
        {
            free_acc_y = gravity / 60;
            free_velocity_y += free_acc_y;
            velocity_y = free_velocity_y
            t.position.y -= free_velocity_y;
            console.log(`free fall with acceleration: ${free_acc_y.toPrecision(4)}
                velocity_y: ${free_velocity_y.toPrecision(4)}
                height: ${t.position.y.toPrecision(4)}`);
            scene.remove(para);
        }
        if (t.position.y <= parachute_jump.value && t.position.y >= 0)
        {
            para.position.x = t.position.x;
            para.position.y = t.position.y - 200;
            para.position.z = t.position.z;
            scene.add(para);
            scene.remove(bag);

            variant = variant + (0.0001 * (t.position.y - (t.position.y - velocity_y)));
            console.log('variant: ' + variant);

            let rope_force = weight_force / rope_counts;
            console.log('rope Force: ' + rope_force);

            let length_range = (rope_force * rope_length) / (rope_area * (3.5 * 1000000000)); // new_rope_length =0.035
            console.log('length range: ' + length_range);

            let all = (rope_length + length_range);
            console.log('all ' + all);

            let current_rope_now = rope_length + variant;
            console.log(`Current rope is ${current_rope_now} at Height ${t.position.y}`)

            if (current_rope_now.toFixed(4) == parseFloat(all.toFixed(4)))
            {
                console.log('current rope is cut on ' + t.position.y);
                parachute_area = 0;
                parachute_mass = 0;
            }

            let air_resistance_force = 0.5 * velocity_y * velocity_y * air_density * parachute_area * cD;
            console.log(`Air resistance Force is ${air_resistance_force}`);

            let acc_y = (weight_force - air_resistance_force) / mass;
            console.log(`a_y in Height: ${t.position.y.toPrecision(4)} is ${acc_y}`);
            velocity_y += acc_y;
            console.log(`velocity_y in Height: ${t.position.y.toPrecision(4)} is ${velocity_y}`);
            t.position.y -= velocity_y;
        }
        // camera.position.set(t.position.x, t.position.y + 100, t.position.z - 500);
        // camera.lookAt(new THREE.Vector3(0, 100, 100).add(man[0].position))
    });

    // test_box.forEach(b => {
    //     b.position.y -= 60 * delta;
    //     console.log(b.position.y);
    // });

    renderer.render(scene, camera);
    requestAnimationFrame(animate);
}

initScene();


